INSERT INTO song VALUES(70,6,6,'La Dernière Valse',180653,replace('La dernière valse LyricsLe bal allait bientôt se terminer\nDevais-je m''en aller ou bien rester ?\nL''orchestre allait jouer le tout dernier morceau\nQuand je t''ai vu passer près de moi...\nC''était la dernière valse\nMon cœur n''était plus sans amour\nEnsemble cette valse\nNous l''avons dansée pour toujours\n\nOn s''est aimé longtemps toujours plus fort\nNos joies nos peines avaient le même accord\nEt puis un jour j''ai vu changer tes yeux\nTu as brisé mon cœur en disant "adieu"\n\nC''était la dernière valse\nMon cœur restait seul sans amour\nEt pourtant cette valse, aurait pu durer toujours\nAinsi va la vie, tout est bien fini\nIl me reste une valse et mes larmes...\nLa la la la la la la la la la\nC''était la dernière valse\nMon cœur restait seul sans amour\nEt pourtant cette valse, aurait pu durer toujours\n\nLa la la la la la la la la laEmbed','\n',char(10)));
INSERT INTO song VALUES(71,6,6,'Le Sable Et La Mer',203334,replace('Le sable et la mer LyricsLes pieds dans le sable blanc il marchait sur la plage\nLe garçon à la peau d''or le garçon de mon âge\nQui ne se doutait pas que j''entrerais dans sa vie\nEt que pour moi l''été mettait son cœur en folie\nPour mieux me le donner pour mieux me le garder\n\nLes pieds dans le sable blanc elle marchait sur la plage\nEn cueillant au bord de l''eau de jolis coquillages\nGuidée par l''étoile de mer elle venait vers mon cœur\nEt sous le ciel bleu elle m''a donné tant de bonheur\nQue depuis ce temps là j''ai le goût le goût de chanter\n\nLe bonheur c''est la mer\nLorsque revient l''été\nLe bonheur c''est qu''on est pu se rencontrer\nSur la plage ensoleillée\n\nLes pieds dans le sable blanc elle marchait sur la plage\nEn cueillant au bord de l''eau de jolis coquillages\nGuidée par l''étoile de mer elle venait vers mon cœur\nEt sous le ciel bleu elle m''a donné tant de bonheur\nQue depuis ce temps là j''ai le goût le goût de chanter\nLes pieds dans le sable blanc il marchait sur la plage\nLe garçon à la peau d''or le garçon de mon âge\nQui ne se doutait pas que j''entrerais dans sa vie\nEt que pour moi l''été mettait son cœur en folie\nPour mieux me le donner pour mieux me le garder\n\nLes pieds dans le sable blanc nous marchions sur la plage\nEn cueillant au bord de l''eau de jolis coquillages\nSans se douter que l''amour était là dans nos vies\nEt que pour nous l''été mettait nos cœurs en folie\nQue depuis ce temps là on a le goût, le goût de chanterEmbed','\n',char(10)));
INSERT INTO song VALUES(72,6,6,'La Leçon D''amour',135807,replace('La leçon d’amour LyricsParmi les leçons de la vie\nLa leçon d''amour est la plus jolie\nÉcoute mon enfant\nUn jour tu seras un homme\nComme ton papa\nEt peut-être que la vie\nNe t''épargnera pas\nTu connaîtras le chagrin\nEt les nuits sans sommeil\nMais dis-toi qu''après la pluie\nSe lève le soleil\nEt même si les hommes\nSont quelque fois méchants\nSouviens-toi mon enfant\nQu''au combat de la vie\nL''amour s''ra triomphant\nGarde-toi de la haine\nC''est trop mesquin pour toi\nFait en sorte que l''amour\nSoit ton unique loi\nMalgré le temps qui passe\nGarde ton cœur d''enfant\nCar il porte la trace\nDe l''amour le plus grand\nEt quand tu seras triste\nRappelle-toi toujours\nQue dans ce monde à nous\nRien de grand ne subsiste\nSans un peu d''amour\nQuand tu seras un homme\nSouviens-toi de ce jour\nDe ce jour merveilleux\nOù tu as appris ta leçon d''amour\nDe ce jour merveilleux\nOù tu as appris ta leçon d''amourEmbed','\n',char(10)));
