PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE artist(
    art_id integer primary key autoincrement not null, -- artist unique id 
    art_name text not null unique, -- artist name 
    img text not null, 
    followers integer not null -- number of followers artist has 
);
CREATE TABLE album(
    id integer primary key autoincrement not null, -- album unique id 
    alb_id integer not null, 
    album_art_id integer not null,
    alb_name text not null, -- album title 
    img text not null, 
    date text not null, -- release date of album 
    total_songs integer not null, -- number of songs in album 
    foreign key (album_art_id) references artist (id)
);
CREATE TABLE song(
    song_id integer primary key autoincrement not null, -- song unique id 
    song_album_id integer not null, 
    song_art_id integer not null, 
    song_name text not null, -- song name 
    duration integer not null, -- length of song 
    lyrics text not null, -- song lyrics 
    foreign key (song_art_id) references artist (art_id)
    foreign key (song_album_id) references album (alb_id)
);
CREATE TABLE genre(
    id integer primary key autoincrement not null, 
    type text not null unique
);
CREATE TABLE album_of(
    id integer primary key autoincrement,
    artist_id text not null,
    album_id text not null, 
    foreign key (artist_id) references artist (id)
    foreign key (album_id) references album (id)
);
CREATE TABLE type_of(
    id integer primary key autoincrement, 
    type_artist_id text not null, 
    type_genre_id text not null, 
    foreign key (type_artist_id) references artist (id)
    foreign key (type_genre_id) references genre (id)
);
DELETE FROM sqlite_sequence;
COMMIT;
